mapper.select:
SELECT
count(DISTINCT a.id)
from
t_article a
LEFT JOIN t_article_tag at on a.id = at.article_id

    is_delete = #{conditionVO.isDelete}

        and article_title like concat('%',#{conditionVO.keywords},'%')


        and `status` = #{conditionVO.status}


        and category_id = #{conditionVO.categoryId}


        and type = #{conditionVO.type}


        and at.tag_id = #{conditionVO.tagId}

SELECT a.id, article_cover, article_title, is_top, is_featured, a.is_delete, a.status, a.type, a.create_time, category_name, t.id AS tag_id, t.tag_name FROM ( SELECT id, article_cover, article_title, is_top, is_featured, is_delete, status, type, create_time, category_id FROM t_article
is_delete = #{conditionVO.isDelete}
and article_title like concat('%',#{conditionVO.keywords},'%')
and status = #{conditionVO.status}
and category_id = #{conditionVO.categoryId}
and type = #{conditionVO.type}
and id in ( SELECT article_id FROM t_article_tag WHERE tag_id = #{conditionVO.tagId} )
ORDER BY is_top DESC, is_featured DESC, id DESC LIMIT #{current},#{size} ) a LEFT JOIN t_category c ON a.category_id = c.id LEFT JOIN t_article_tag at ON a.id = at.article_id LEFT JOIN t_tag t ON t.id = at.tag_id ORDER BY is_top DESC, is_featured DESC, a.id DESC
