SELECT u.nickname, u.avatar, u.website, c.user_id, c.id, c.comment_content, c.create_time FROM t_comment c JOIN t_user_info u ON c.user_id = u.id
topic_id = #{commentVO.topicId}
AND type = #{commentVO.type} AND c.is_review = 1 AND parent_id IS NULL
ORDER BY c.id DESC LIMIT #{current},#{size}

SELECT * FROM ( SELECT c.user_id, u.nickname, u.avatar, u.website, c.reply_user_id, r.nickname AS reply_nickname, r.website AS reply_website, c.id, c.parent_id, c.comment_content, c.create_time, row_number () over ( PARTITION BY parent_id ORDER BY create_time ASC ) row_num FROM t_comment c JOIN t_user_info u ON c.user_id = u.id JOIN t_user_info r ON c.reply_user_id = r.id WHERE c.is_review = 1 AND parent_id IN (
#{commentId}
) order by c.create_time desc ) t

SELECT count(1) from t_comment c LEFT JOIN t_user_info u ON c.user_id = u.id
c.type = #{conditionVO.type}
and c.is_review = #{conditionVO.isReview}
and u.nickname like concat('%',#{conditionVO.keywords},'%')

SELECT c.id, u.avatar, u.nickname, r.nickname AS reply_nickname, a.article_title, c.comment_content, c.type, c.create_time, c.is_review FROM t_comment c LEFT JOIN t_article a ON c.topic_id = a.id LEFT JOIN t_user_info u ON c.user_id = u.id LEFT JOIN t_user_info r ON c.reply_user_id = r.id
c.type = #{conditionVO.type}
and c.is_review = #{conditionVO.isReview}
and u.nickname like concat('%',#{conditionVO.keywords},'%')
ORDER BY id DESC LIMIT #{current},#{size}

SELECT topic_id as id, COUNT( 1 ) AS comment_count FROM t_comment WHERE type = #{type} AND topic_id IN
#{topicId}
GROUP BY topic_id

SELECT topic_id as id,
       COUNT(1) AS comment_count
FROM t_comment
WHERE type = #{type}
  AND topic_id = #{topicId}
GROUP BY topic_id
